mapper.insert:
insert into calendar(id,title,cdate,ctime,num,color) values(#{id},#{title}, to_date(#{date},'YYYY/MM/DD'), 
#{time}
null, calendar_seq.nextval, #{color})

mapper.update:
update calendar set title=#{title}, cdate=#{date}, 
ctime=#{time}
ctime=null, color=#{color} where num=#{update}
