mapper.select:
select count(*) from questionboard 
 where

	title like #{search} or content like #{search}

select t.*, to_date(to_char(sysdate))+1-ft_date as FT_DIFF from (select d.* from (select rownum as r, questionboard.* from questionboard where
ft_title like #{search} or ft_content like #{search}
order by ft_num desc)d where r between #{start} and #{end})t
